import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaTrash, FaEdit, FaCheck, FaPlus, FaSun, FaMoon } from 'react-icons/fa';
import './App.css';

function App() {
    const [todos, setTodos] = useState([]);
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [dueDate, setDueDate] = useState('');
    const [priority, setPriority] = useState('Medium');
    const [editId, setEditId] = useState(null);
    const [darkMode, setDarkMode] = useState(false);
    const [filter, setFilter] = useState('All');

    useEffect(() => {
        fetchTodos();
    }, []);

    const fetchTodos = async () => {
        try {
            const response = await axios.get('http://127.0.0.1:8000/api/todos/');
            setTodos(response.data);
        } catch (error) {
            console.error('Error fetching todos:', error);
        }
    };

    const handleSubmit = async () => {
        if (!title.trim()) return;

        const newTodo = { title, description, dueDate, priority, completed: false };

        try {
            if (editId) {
                const response = await axios.put(`http://127.0.0.1:8000/api/todos/${editId}/`, newTodo);
                setTodos(todos.map(todo => (todo.id === editId ? response.data : todo)));
            } else {
                const response = await axios.post('http://127.0.0.1:8000/api/todos/', newTodo);
                setTodos([...todos, response.data]);
            }
            resetForm();
        } catch (error) {
            console.error('Error saving todo:', error);
        }
    };

    const resetForm = () => {
        setTitle('');
        setDescription('');
        setDueDate('');
        setPriority('Medium');
        setEditId(null);
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://127.0.0.1:8000/api/todos/${id}/`);
            setTodos(todos.filter(todo => todo.id !== id));
        } catch (error) {
            console.error('Error deleting todo:', error);
        }
    };

    const toggleComplete = async (id) => {
        const todo = todos.find(todo => todo.id === id);
        try {
            const response = await axios.put(`http://127.0.0.1:8000/api/todos/${id}/`, { ...todo, completed: !todo.completed });
            setTodos(todos.map(todo => (todo.id === id ? response.data : todo)));
        } catch (error) {
            console.error('Error updating todo:', error);
        }
    };

    const filteredTodos = todos.filter(todo => {
        if (filter === 'Completed') return todo.completed;
        if (filter === 'Pending') return !todo.completed;
        return true;
    });

    return (
        <div className={`App ${darkMode ? 'dark' : ''}`}>
            <div className="header">
                <h1>To-Do List</h1>
                <button className="dark-mode-toggle" onClick={() => setDarkMode(!darkMode)}>
                    {darkMode ? <FaSun /> : <FaMoon />}
                </button>
            </div>
            <div className="todo-form">
                <input type="text" placeholder="Title" value={title} onChange={(e) => setTitle(e.target.value)} />
                <textarea placeholder="Description" value={description} onChange={(e) => setDescription(e.target.value)} />
                <input type="date" value={dueDate} onChange={(e) => setDueDate(e.target.value)} />
                <select value={priority} onChange={(e) => setPriority(e.target.value)}>
                    <option value="Low">Low</option>
                    <option value="Medium">Medium</option>
                    <option value="High">High</option>
                </select>
                <button onClick={handleSubmit}>{editId ? <FaEdit /> : <FaPlus />} {editId ? 'Update' : 'Add'}</button>
            </div>
            <div className="filter-buttons">
                {['All', 'Completed', 'Pending'].map(status => (
                    <button key={status} onClick={() => setFilter(status)}>{status}</button>
                ))}
            </div>
            <ul className="todo-list">
                {filteredTodos.map(todo => (
                    <li key={todo.id} className={`todo-item ${todo.completed ? 'completed' : ''}`}>
                        <div className="todo-content">
                            <h3>{todo.title}</h3>
                            <p>{todo.description}</p>
                            <p>Due: {todo.dueDate}</p>
                            <p>Priority: {todo.priority}</p>
                        </div>
                        <div className="todo-actions">
                            <button onClick={() => toggleComplete(todo.id)}><FaCheck /></button>
                            <button onClick={() => { setEditId(todo.id); setTitle(todo.title); setDescription(todo.description); setDueDate(todo.dueDate); setPriority(todo.priority); }}><FaEdit /></button>
                            <button onClick={() => handleDelete(todo.id)}><FaTrash /></button>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default App;
