import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaTrash, FaEdit, FaCheck, FaPlus, FaSun, FaMoon, FaTags } from 'react-icons/fa';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import Confetti from 'react-confetti';
import './App.css';

function App() {
    const [todos, setTodos] = useState([]);
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [dueDate, setDueDate] = useState('');
    const [priority, setPriority] = useState('Medium');
    const [category, setCategory] = useState('Work');
    const [editId, setEditId] = useState(null);
    const [error, setError] = useState('');
    const [darkMode, setDarkMode] = useState(false);
    const [filter, setFilter] = useState('All');
    const [searchQuery, setSearchQuery] = useState('');
    const [showConfetti, setShowConfetti] = useState(false);

    useEffect(() => {
        fetchTodos();
    }, []);

    const fetchTodos = async () => {
        try {
            const response = await axios.get('http://127.0.0.1:8000/api/todos/');
            setTodos(response.data);
        } catch (err) {
            setError('Failed to fetch todos. Please check the backend server.');
            console.error(err);
        }
    };

    const addTodo = async () => {
        if (!title.trim()) {
            setError('Title cannot be empty.');
            return;
        }
        try {
            const response = await axios.post('http://127.0.0.1:8000/api/todos/', {
                title,
                description,
                dueDate,
                priority,
                category,
                completed: false,
            });
            setTodos([...todos, response.data]);
            setTitle('');
            setDescription('');
            setDueDate('');
            setPriority('Medium');
            setCategory('Work');
            setError('');
        } catch (err) {
            setError('Failed to add todo. Please check the backend server.');
            console.error(err);
        }
    };

    const deleteTodo = async (id) => {
        try {
            await axios.delete(`http://127.0.0.1:8000/api/todos/${id}/`);
            setTodos(todos.filter(todo => todo.id !== id));
        } catch (err) {
            setError('Failed to delete todo. Please check the backend server.');
            console.error(err);
        }
    };

    const toggleComplete = async (id) => {
        try {
            const todo = todos.find(todo => todo.id === id);
            const response = await axios.put(`http://127.0.0.1:8000/api/todos/${id}/`, {
                ...todo,
                completed: !todo.completed,
            });
            setTodos(todos.map(todo => (todo.id === id ? response.data : todo)));
            if (!todo.completed) {
                setShowConfetti(true);
                setTimeout(() => setShowConfetti(false), 3000);
            }
        } catch (err) {
            setError('Failed to update todo. Please check the backend server.');
            console.error(err);
        }
    };

    const editTodo = (id) => {
        const todo = todos.find(todo => todo.id === id);
        setTitle(todo.title);
        setDescription(todo.description);
        setDueDate(todo.dueDate);
        setPriority(todo.priority);
        setCategory(todo.category);
        setEditId(id);
    };

    const updateTodo = async () => {
        try {
            const response = await axios.put(`http://127.0.0.1:8000/api/todos/${editId}/`, {
                title,
                description,
                dueDate,
                priority,
                category,
                completed: todos.find(todo => todo.id === editId).completed,
            });
            setTodos(todos.map(todo => (todo.id === editId ? response.data : todo)));
            setTitle('');
            setDescription('');
            setDueDate('');
            setPriority('Medium');
            setCategory('Work');
            setEditId(null);
        } catch (err) {
            setError('Failed to update todo. Please check the backend server.');
            console.error(err);
        }
    };

    const onDragEnd = (result) => {
        if (!result.destination) return;
        const items = Array.from(todos);
        const [reorderedItem] = items.splice(result.source.index, 1);
        items.splice(result.destination.index, 0, reorderedItem);
        setTodos(items);
    };

    const filteredTodos = todos.filter(todo => {
        if (filter === 'Completed') return todo.completed;
        if (filter === 'Pending') return !todo.completed;
        return true;
    }).filter(todo => todo.title.toLowerCase().includes(searchQuery.toLowerCase()));

    const progress = (todos.filter(todo => todo.completed).length / todos.length) * 100;

    return (
        <div className={`App ${darkMode ? 'dark' : ''}`}>
            {showConfetti && <Confetti />}
            <h1>My Creative Todo App</h1>
            <button className="dark-mode-toggle" onClick={() => setDarkMode(!darkMode)}>
                {darkMode ? <FaSun /> : <FaMoon />}
            </button>
            {error && <p className="error">{error}</p>}
            <div className="todo-form">
                <input
                    type="text"
                    placeholder="Title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Description"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                />
                <input
                    type="date"
                    value={dueDate}
                    onChange={(e) => setDueDate(e.target.value)}
                />
                <select value={priority} onChange={(e) => setPriority(e.target.value)}>
                    <option value="Low">Low</option>
                    <option value="Medium">Medium</option>
                    <option value="High">High</option>
                </select>
                <select value={category} onChange={(e) => setCategory(e.target.value)}>
                    <option value="Work">Work</option>
                    <option value="Personal">Personal</option>
                    <option value="Shopping">Shopping</option>
                    <option value="Other">Other</option>
                </select>
                <button onClick={editId ? updateTodo : addTodo}>
                    {editId ? <FaEdit /> : <FaPlus />} {editId ? 'Update Todo' : 'Add Todo'}
                </button>
            </div>
            <div className="filter-buttons">
                <button onClick={() => setFilter('All')}>All</button>
                <button onClick={() => setFilter('Completed')}>Completed</button>
                <button onClick={() => setFilter('Pending')}>Pending</button>
            </div>
            <input
                type="text"
                placeholder="Search tasks..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="search-bar"
            />
            <div className="progress-bar">
                <div className="progress" style={{ width: `${progress}%` }}></div>
            </div>
            <DragDropContext onDragEnd={onDragEnd}>
                <Droppable droppableId="todos">
                    {(provided) => (
                        <ul className="todo-list" {...provided.droppableProps} ref={provided.innerRef}>
                            {filteredTodos.map((todo, index) => (
                                <Draggable key={todo.id} draggableId={todo.id.toString()} index={index}>
                                    {(provided) => (
                                        <li
                                            className={`todo-item ${todo.completed ? 'completed' : ''}`}
                                            ref={provided.innerRef}
                                            {...provided.draggableProps}
                                            {...provided.dragHandleProps}
                                        >
                                            <div className="todo-content">
                                                <h3>{todo.title}</h3>
                                                <p>{todo.description}</p>
                                                <p>Due: {todo.dueDate}</p>
                                                <p>Priority: {todo.priority}</p>
                                                <p>Category: {todo.category}</p>
                                            </div>
                                            <div className="todo-actions">
                                                <button onClick={() => toggleComplete(todo.id)}>
                                                    <FaCheck />
                                                </button>
                                                <button onClick={() => editTodo(todo.id)}>
                                                    <FaEdit />
                                                </button>
                                                <button onClick={() => deleteTodo(todo.id)}>
                                                    <FaTrash />
                                                </button>
                                            </div>
                                        </li>
                                    )}
                                </Draggable>
                            ))}
                            {provided.placeholder}
                        </ul>
                    )}
                </Droppable>
            </DragDropContext>
        </div>
    );
}

export default App;
