import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaTrash, FaEdit, FaCheck, FaPlus, FaSun, FaMoon } from 'react-icons/fa';
import './App.css';

function App() {
    // State variables
    const [todos, setTodos] = useState([]); // Holds the list of todos
    const [title, setTitle] = useState(''); // Holds the title of a new or edited todo
    const [description, setDescription] = useState(''); // Holds the description of a new or edited todo
    const [dueDate, setDueDate] = useState(''); // Holds the due date of a new or edited todo
    const [priority, setPriority] = useState('Medium'); // Holds the priority of a new or edited todo
    const [editId, setEditId] = useState(null); // Tracks the ID of the todo being edited
    const [error, setError] = useState(''); // Holds error messages
    const [darkMode, setDarkMode] = useState(false); // Toggles dark mode
    const [filter, setFilter] = useState('All'); // Filters todos based on completion status

    // Fetch todos from the backend when the component mounts
    useEffect(() => {
        fetchTodos();
    }, []);

    // Function to fetch todos from the backend
    const fetchTodos = async () => {
        try {
            const response = await axios.get('http://127.0.0.1:8000/api/todos/');
            setTodos(response.data); // Update the todos state with the fetched data
        } catch (err) {
            setError('Failed to fetch todos. Please check the backend server.');
            console.error(err);
        }
    };

    // Function to add a new todo
    const addTodo = async () => {
        if (!title.trim()) {
            setError('Title cannot be empty.');
            return;
        }
        try {
            const response = await axios.post('http://127.0.0.1:8000/api/todos/', {
                title,
                description,
                dueDate,
                priority,
                completed: false,
            });
            setTodos([...todos, response.data]); // Add the new todo to the list
            resetForm(); // Clear the form fields
            setError(''); // Clear any errors
        } catch (err) {
            setError('Failed to add todo. Please check the backend server.');
            console.error(err);
        }
    };

    // Function to delete a todo
    const deleteTodo = async (id) => {
        try {
            await axios.delete(`http://127.0.0.1:8000/api/todos/${id}/`);
            setTodos(todos.filter(todo => todo.id !== id)); // Remove the deleted todo from the list
        } catch (err) {
            setError('Failed to delete todo. Please check the backend server.');
            console.error(err);
        }
    };

    // Function to toggle the completion status of a todo
    const toggleComplete = async (id) => {
        try {
            const todo = todos.find(todo => todo.id === id);
            const response = await axios.put(`http://127.0.0.1:8000/api/todos/${id}/`, {
                ...todo,
                completed: !todo.completed,
            });
            setTodos(todos.map(todo => (todo.id === id ? response.data : todo))); // Update the todo in the list
        } catch (err) {
            setError('Failed to update todo. Please check the backend server.');
            console.error(err);
        }
    };

    // Function to populate the form with the details of a todo for editing
    const editTodo = (id) => {
        const todo = todos.find(todo => todo.id === id);
        setTitle(todo.title);
        setDescription(todo.description);
        setDueDate(todo.dueDate);
        setPriority(todo.priority);
        setEditId(id); // Set the ID of the todo being edited
    };

    // Function to update an existing todo
    const updateTodo = async () => {
        try {
            const response = await axios.put(`http://127.0.0.1:8000/api/todos/${editId}/`, {
                title,
                description,
                dueDate,
                priority,
                completed: todos.find(todo => todo.id === editId).completed,
            });
            setTodos(todos.map(todo => (todo.id === editId ? response.data : todo))); // Update the todo in the list
            resetForm(); // Clear the form fields
            setEditId(null); // Reset the edit ID
        } catch (err) {
            setError('Failed to update todo. Please check the backend server.');
            console.error(err);
        }
    };

    // Function to reset the form fields
    const resetForm = () => {
        setTitle('');
        setDescription('');
        setDueDate('');
        setPriority('Medium');
    };

    // Filter todos based on the selected filter
    const filteredTodos = todos.filter(todo => {
        if (filter === 'Completed') return todo.completed;
        if (filter === 'Pending') return !todo.completed;
        return true;
    });

    return (
        <div className={`App ${darkMode ? 'dark' : ''}`}>
            <h1>My To-do App</h1>
            {/* Dark mode toggle button */}
            <button className="dark-mode-toggle" onClick={() => setDarkMode(!darkMode)}>
                {darkMode ? <FaSun /> : <FaMoon />}
            </button>
            {/* Display error messages */}
            {error && <p className="error">{error}</p>}
            {/* Todo input form */}
            <div className="todo-form">
                <input
                    type="text"
                    placeholder="Title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Description"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                />
                <input
                    type="date"
                    value={dueDate}
                    onChange={(e) => setDueDate(e.target.value)}
                />
                <select value={priority} onChange={(e) => setPriority(e.target.value)}>
                    <option value="Low">Low</option>
                    <option value="Medium">Medium</option>
                    <option value="High">High</option>
                </select>
                <button onClick={editId ? updateTodo : addTodo}>
                    {editId ? <FaEdit /> : <FaPlus />} {editId ? 'Update Todo' : 'Add Todo'}
                </button>
            </div>
            {/* Filter buttons */}
            <div className="filter-buttons">
                <button onClick={() => setFilter('All')}>All</button>
                <button onClick={() => setFilter('Completed')}>Completed</button>
                <button onClick={() => setFilter('Pending')}>Pending</button>
            </div>
            {/* Todo list */}
            <ul className="todo-list">
                {filteredTodos.map(todo => (
                    <li key={todo.id} className={`todo-item ${todo.completed ? 'completed' : ''}`}>
                        <div className="todo-content">
                            <h3>{todo.title}</h3>
                            <p>{todo.description}</p>
                            <p>Due: {todo.dueDate}</p>
                            <p>Priority: {todo.priority}</p>
                        </div>
                        <div className="todo-actions">
                            <button onClick={() => toggleComplete(todo.id)}>
                                <FaCheck />
                            </button>
                            <button onClick={() => editTodo(todo.id)}>
                                <FaEdit />
                            </button>
                            <button onClick={() => deleteTodo(todo.id)}>
                                <FaTrash />
                            </button>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default App;
